import React, { useState } from 'react';
import { User, Upload, X } from 'lucide-react';
import { locationSuggestions } from '../utils/suggestions';

export default function PersonalInfoForm({ formData, handleInputChange, errors, setFormData }) {
  const [showPhotoConsent, setShowPhotoConsent] = useState(false);

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file size (max 2MB)
      if (file.size > 2 * 1024 * 1024) {
        alert('Image size should be less than 2MB');
        return;
      }

      // Check file type
      if (!file.type.startsWith('image/')) {
        alert('Please upload an image file');
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        setShowPhotoConsent(true);
        // Temporarily store the image
        setFormData(prev => ({ ...prev, tempProfilePhoto: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleConsentYes = () => {
    setFormData(prev => ({ 
      ...prev, 
      profilePhoto: prev.tempProfilePhoto,
      tempProfilePhoto: null 
    }));
    setShowPhotoConsent(false);
  };

  const handleConsentNo = () => {
    setFormData(prev => ({ 
      ...prev, 
      tempProfilePhoto: null 
    }));
    setShowPhotoConsent(false);
  };

  const removePhoto = () => {
    setFormData(prev => ({ ...prev, profilePhoto: null }));
  };

  return (
    <div className="bg-white rounded-xl shadow-sm border p-6">
      <div className="flex items-center gap-2 mb-4">
        <User className="w-5 h-5 text-blue-500" />
        <h2 className="text-xl font-bold">Personal Information</h2>
      </div>

      {/* Profile Photo Section */}
      <div className="mb-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg border border-blue-200">
        <div className="flex items-start gap-4">
          <div className="flex-shrink-0">
            {formData.profilePhoto ? (
              <div className="relative">
                <img 
                  src={formData.profilePhoto} 
                  alt="Profile" 
                  className="w-24 h-24 rounded-full object-cover border-4 border-white shadow-lg"
                />
                <button
                  onClick={removePhoto}
                  className="absolute -top-2 -right-2 p-1 bg-red-500 text-white rounded-full hover:bg-red-600 shadow-lg"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
            ) : (
              <div className="w-24 h-24 rounded-full bg-gradient-to-br from-blue-400 to-purple-400 flex items-center justify-center border-4 border-white shadow-lg">
                <User className="w-12 h-12 text-white" />
              </div>
            )}
          </div>
          <div className="flex-1">
            <h3 className="font-semibold text-slate-900 mb-1">Profile Picture (Optional)</h3>
            <p className="text-sm text-slate-600 mb-3">
              Add a professional photo to make your resume stand out. We'll ask for your consent before adding it.
            </p>
            {!formData.profilePhoto && (
              <label className="flex items-center gap-2 px-4 py-2 bg-white border-2 border-blue-300 text-blue-600 rounded-lg hover:bg-blue-50 cursor-pointer transition-colors w-fit">
                <Upload className="w-4 h-4" />
                <span className="text-sm font-medium">Upload Photo</span>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handlePhotoUpload}
                  className="hidden"
                />
              </label>
            )}
          </div>
        </div>
      </div>

      {/* Consent Modal */}
      {showPhotoConsent && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 no-print">
          <div className="bg-white rounded-xl shadow-2xl p-6 max-w-md mx-4">
            <h3 className="text-xl font-bold text-slate-900 mb-3">Add Profile Picture?</h3>
            <p className="text-slate-700 mb-4">
              Do you want to include your profile picture in your resume? This will be visible to recruiters and employers.
            </p>
            <div className="flex justify-center mb-4">
              <img 
                src={formData.tempProfilePhoto} 
                alt="Preview" 
                className="w-32 h-32 rounded-full object-cover border-4 border-blue-200"
              />
            </div>
            <div className="flex gap-3">
              <button
                onClick={handleConsentNo}
                className="flex-1 px-4 py-2 bg-slate-200 text-slate-700 rounded-lg hover:bg-slate-300 font-medium"
              >
                No, Skip
              </button>
              <button
                onClick={handleConsentYes}
                className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-lg hover:from-blue-600 hover:to-purple-700 font-medium"
              >
                Yes, Add Photo
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <input
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            placeholder="Full Name *"
            className={`w-full px-4 py-2 border rounded-lg ${errors.fullName ? 'border-red-500' : 'border-slate-300'}`}
          />
          {errors.fullName && <p className="text-red-500 text-xs mt-1">{errors.fullName}</p>}
        </div>
        <div>
          <input
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            placeholder="Email"
            className={`w-full px-4 py-2 border rounded-lg ${errors.email ? 'border-red-500' : 'border-slate-300'}`}
          />
          {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
        </div>
        <div>
          <input
            name="phone"
            value={formData.phone}
            onChange={handleInputChange}
            placeholder="Phone (e.g., +1-234-567-8900)"
            className={`w-full px-4 py-2 border rounded-lg ${errors.phone ? 'border-red-500' : 'border-slate-300'}`}
          />
          {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
        </div>
        <div>
          <input
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            placeholder="Location (e.g., New York, USA)"
            list="location-list"
            className={`w-full px-4 py-2 border rounded-lg ${errors.location ? 'border-red-500' : 'border-slate-300'}`}
          />
          <datalist id="location-list">
            {locationSuggestions.map((loc, idx) => (
              <option key={idx} value={loc} />
            ))}
          </datalist>
          {errors.location && <p className="text-red-500 text-xs mt-1">{errors.location}</p>}
        </div>
        <div>
          <input
            name="linkedin"
            value={formData.linkedin}
            onChange={handleInputChange}
            placeholder="LinkedIn URL (e.g., linkedin.com/in/username)"
            className={`w-full px-4 py-2 border rounded-lg ${errors.linkedin ? 'border-red-500' : 'border-slate-300'}`}
          />
          {errors.linkedin && <p className="text-red-500 text-xs mt-1">{errors.linkedin}</p>}
        </div>
        <div>
          <input
            name="github"
            value={formData.github}
            onChange={handleInputChange}
            placeholder="GitHub URL (e.g., github.com/username)"
            className={`w-full px-4 py-2 border rounded-lg ${errors.github ? 'border-red-500' : 'border-slate-300'}`}
          />
          {errors.github && <p className="text-red-500 text-xs mt-1">{errors.github}</p>}
        </div>
        <div className="md:col-span-2">
          <input
            name="portfolio"
            value={formData.portfolio}
            onChange={handleInputChange}
            placeholder="Portfolio URL (e.g., yourwebsite.com)"
            className={`w-full px-4 py-2 border rounded-lg ${errors.portfolio ? 'border-red-500' : 'border-slate-300'}`}
          />
          {errors.portfolio && <p className="text-red-500 text-xs mt-1">{errors.portfolio}</p>}
        </div>
      </div>
    </div>
  );
}
