import React from 'react';
import { Sparkles } from 'lucide-react';

const colorSchemes = {
  blue: { primary: '#3b82f6', secondary: '#2563eb', light: '#dbeafe' },
  green: { primary: '#10b981', secondary: '#059669', light: '#d1fae5' },
  purple: { primary: '#a855f7', secondary: '#9333ea', light: '#ede9fe' },
  red: { primary: '#ef4444', secondary: '#dc2626', light: '#fee2e2' }
};

export default function Sidebar({ formData, setFormData, onGenerateResume }) {
  return (
    <div className="bg-white rounded-xl shadow-sm border p-6 sticky top-6">
      <h3 className="text-lg font-bold mb-4">Customize Style</h3>
      
      <div className="mb-4">
        <label className="block text-sm font-medium mb-2">Color Theme</label>
        <div className="grid grid-cols-4 gap-2">
          {Object.keys(colorSchemes).map(color => (
            <button
              key={color}
              onClick={() => setFormData(prev => ({ ...prev, color }))}
              className={`h-10 rounded-lg border-2 transition ${
                formData.color === color ? 'border-slate-900 scale-110' : 'border-slate-200'
              }`}
              style={{ backgroundColor: colorSchemes[color].primary }}
            />
          ))}
        </div>
      </div>

      <button
        onClick={onGenerateResume}
        className="w-full py-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white font-semibold rounded-lg hover:from-blue-600 hover:to-purple-700 flex items-center justify-center gap-2"
      >
        <Sparkles className="w-5 h-5" />
        Generate Resume
      </button>

      <div className="mt-6 p-4 bg-blue-50 rounded-lg">
        <h4 className="font-semibold text-blue-900 mb-2">AI Features</h4>
        <ul className="text-sm text-blue-800 space-y-1">
          <li>• Multi-language translation</li>
          <li>• Roman/Hinglish conversion</li>
          <li>• Professional formatting</li>
          <li>• Action verb optimization</li>
          <li>• Skills categorization</li>
          <li>• ATS-friendly</li>
          <li>• URL validation</li>
          <li>• Smart suggestions</li>
        </ul>
      </div>
    </div>
  );
}
