import { translateToEnglish } from './translator';

export const enhanceDescription = (text) => {
  if (!text || !text.trim()) return '';
  
  const words = text.trim().split(/\s+/);
  
  if (words.length <= 3) {
    const actionVerbs = {
      'code': 'Developed and maintained clean, efficient code',
      'coding': 'Implemented robust coding solutions',
      'design': 'Designed intuitive and user-friendly interfaces',
      'test': 'Conducted comprehensive testing and quality assurance',
      'debug': 'Debugged and resolved complex technical issues',
      'lead': 'Led cross-functional teams to achieve project objectives',
      'manage': 'Managed project timelines and deliverables effectively',
      'research': 'Conducted thorough research and analysis',
      'develop': 'Developed innovative solutions to meet business requirements',
      'build': 'Built scalable and maintainable applications',
      'create': 'Created comprehensive documentation and technical specifications',
      'implement': 'Implemented best practices and industry standards',
      'optimize': 'Optimized system performance and efficiency',
      'collaborate': 'Collaborated with stakeholders to define project requirements',
      'analyze': 'Analyzed data and provided actionable insights'
    };
    
    const lowerText = text.toLowerCase().trim();
    for (const [key, value] of Object.entries(actionVerbs)) {
      if (lowerText.includes(key)) {
        return value;
      }
    }
    
    return `Successfully handled ${text} responsibilities and contributed to team objectives`;
  }
  
  const actionVerbs = ['Developed', 'Implemented', 'Designed', 'Led', 'Managed', 'Created', 'Built', 'Optimized', 'Analyzed', 'Collaborated'];
  const startsWithActionVerb = actionVerbs.some(verb => text.trim().startsWith(verb));
  
  if (!startsWithActionVerb) {
    return `Contributed to ${text}`;
  }
  
  return text;
};

export const enhanceProjectDescription = (description, technologies) => {
  if (!description || description.trim().length < 10) {
    if (technologies) {
      return `Developed a comprehensive solution utilizing ${technologies} to deliver a robust and scalable application. Implemented best practices and modern design patterns to ensure code quality and maintainability.`;
    }
    return 'Developed a feature-rich application demonstrating technical expertise and problem-solving capabilities. Implemented modern development practices and delivered a high-quality solution.';
  }
  return translateToEnglish(description);
};

export const enhanceEducationDescription = (degree, institution) => {
  if (!degree || !institution) return '';
  return `Completed ${degree} program with focus on core competencies and practical applications. Developed strong analytical and problem-solving skills through coursework and projects.`;
};

export const generateEnhancedSummary = (data) => {
  const { currentRole, yearsExperience, targetRole, technicalSkills } = data;
  const skills = technicalSkills.split(',').slice(0, 4).map(s => s.trim()).filter(Boolean).join(', ');
  
  if (!currentRole && !targetRole && !skills) {
    return 'Dynamic and results-oriented professional with a proven track record of delivering high-impact results across diverse projects. Combines strong technical expertise with excellent problem-solving abilities to drive innovation and exceed organizational goals. Demonstrated ability to adapt to new technologies and methodologies while maintaining focus on quality and efficiency. Committed to continuous learning and professional development to stay current with industry trends and best practices.';
  }
  
  const exp = yearsExperience ? `${yearsExperience}+ years` : 'proven';
  const role = currentRole || 'professional';
  const target = targetRole || 'senior technical leadership';
  const skillSection = skills ? ` with expertise in ${skills}` : '';
  
  return `Accomplished ${role} with ${exp} of progressive experience in delivering innovative solutions and driving measurable business impact${skillSection}. Demonstrated success in leading complex projects from conception to deployment, consistently exceeding performance expectations and stakeholder requirements. Strong analytical and problem-solving capabilities combined with excellent communication skills enable effective collaboration across cross-functional teams. Proven ability to mentor junior team members and contribute to organizational growth through technical excellence and strategic thinking. Currently seeking to leverage comprehensive skill set and experience to excel as ${target}, bringing fresh perspectives and driving continuous improvement initiatives.`;
};
